function editSatuanUnitDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-SatuanUnit-Edit').modal('show');

    $('#Edit-SatuanUnit-Kode').val(data.Kode);
    $('#Edit-SatuanUnit-Nama').val(data.Name);
    $('#Edit-SatuanUnit-Status').prop('checked', data.Active);


    $('#md-SatuanUnit-Edit').data('id', data.Id);
    $('#SatuanUnit-Edit_btn').unbind();
    $('#SatuanUnit-Edit_btn').on('click', function () {
        editSatuanUnitSave();
    });
}

function editSatuanUnitSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
                Active: $('#Edit-SatuanUnit-Status').is(":checked"),
                Kode: $('#Edit-SatuanUnit-Kode').val(),
                Name: $('#Edit-SatuanUnit-Nama').val()
            }
            RequestData('PUT', `/v1/SatuanJenis/edit/${$('#md-SatuanUnit-Edit').data('id')}`, '#md-SatuanUnit-Edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.Succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-SatuanUnit-Edit').modal('hide');
                    getListSatuanUnit();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
