function deletePenawaranDialog(el) {
    var data = $(el).data('detail');
    ConfirmMessage('Apakah Anda Yakin Akan Menghapus Data Ini?', isConfirm => {
        if (isConfirm) {
            var element = {
                tbody: '#PenawaranExternal-tbody',
                tcontainer: '#PenawaranExternal-table',
            };
            RequestData('DELETE', `/v1/Penawaran/delete/${data.Id}`, element.tcontainer, element.tbody, null, function (data) {
                if (data.Succeeded) {
                    ShowNotif("Data Deleted Successfully ...", "success");
                    getListPenawaranProgramExternal();
                    getListPenawaranExternal();
                    getListHistoryExternal();
                    getListPenawaranExternalFilter();
                    getListHistoryExternalFilter();
                } else
                    ShowNotif(`${data.message} : ${data.description}`, "error");
            });
        }
    });
}
