function deletePenawaranDetailDialog(el) {
    var getUrl = (window.location).href;
    var id = getUrl.substring(getUrl.lastIndexOf('=') + 1);

/*    var data = $(el).data('detail');*/
    ConfirmMessage('Apakah Anda Yakin Akan Menghapus Data Ini?', isConfirm => {
        if (isConfirm) {
            var element = {
                tbody: '#PenawaranExternal-tbody',
                tcontainer: '#PenawaranExternal-table',
            };
            RequestData('DELETE', `/v1/Penawaran/delete/${id}`, element.tcontainer, element.tbody, null, function (data) {
                if (data.Succeeded) {
                    ShowNotif("Data Deleted Successfully ...", "success");
                    detailPengajuanDialog();
                } else
                    ShowNotif(`${data.message} : ${data.description}`, "error");
            });
        }
    });
}
